/*==================================================
src/components/Credits.js

The Credits component contains information for Credits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import {Link} from 'react-router-dom';

const Credits = (props) => {
  // Create the list of Credit items
  let creditsView = () => {
    const { credits } = props;
    return credits.map((credit) => {  // Extract "id", "amount", "description" and "date" properties of each credit JSON array element
      let date = credit.date.slice(0,10);
      // Format amount to 2 decimal places
      let amount = Number(credit.amount).toFixed(2);
      return <li key={credit.id}>{amount} {credit.description} {date}</li>
    });
  }

  // Handler for form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const description = e.target.elements.description.value;
    const amount = parseFloat(e.target.elements.amount.value).toFixed(2);
    const date = new Date().toISOString().slice(0,10); // Get current date in yyyy-mm-dd format
    
    props.addCredit({
      id: crypto.randomUUID(), // Generate unique ID
      description: description,
      amount: amount,
      date: date
    });

    // Clear form fields
    e.target.elements.description.value = '';
    e.target.elements.amount.value = '';
  }

  // Render the list of Credit items and a form to input new Credit item
  return (
    <div>
      <h1>Credits</h1>

      {creditsView()}

      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="description" 
          placeholder="Description" 
          required
        />
        <input 
          type="number" 
          name="amount" 
          step="0.01" 
          min="0" 
          placeholder="Amount" 
          required
        />
        <button type="submit">Add Credit</button>
      </form>

      <div>
        Balance: {Number(props.balance).toFixed(2)}
      </div>
      
      <br/>
      <Link to="/">Return to Home</Link>
    </div>
  );
}

export default Credits;
