/*==================================================
src/components/AccountBalance.js

The AccountBalance component displays account balance. It is included in other page views.
==================================================*/
import React, {Component} from 'react';

const AccountBalance = (props) => {
  const calculateBalance = () => {
    const totalCredits = props.credits?.reduce((sum, credit) => sum + credit.amount, 0) || 0;
    const totalDebits = props.debits?.reduce((sum, debit) => sum + debit.amount, 0) || 0;
    return totalCredits - totalDebits;
  }

  const formatBalance = (balance) => {
    const absBalance = Math.abs(balance);
    const formattedNumber = absBalance.toFixed(2);
    return balance < 0 ? `-$${formattedNumber}` : `$${formattedNumber}`;
  }

  const currentBalance = calculateBalance();
  const formattedBalance = formatBalance(currentBalance);

  return (
    <div>
      <h2>Account Balance</h2>
      <div>{formattedBalance}</div>
    </div>
  );
}

export default AccountBalance;
