/*==================================================
src/components/Debits.js

The Debits component contains information for Debits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import {Link} from 'react-router-dom';

const Debits = (props) => {
  let debitsView = () => {
    const { debits } = props;
    return debits.map((debit) => {
      let date = debit.date.slice(0,10);
      return <li key={debit.id}>${debit.amount.toFixed(2)} {debit.description} {date}</li>
    });
  }

  return (
    <div>
      <h1>Debits</h1>
      <AccountBalance credits={props.credits} debits={props.debits} />
      {debitsView()}
      <form onSubmit={props.addDebit}>
        <input type="text" name="description" />
        <input type="number" step="0.01" name="amount" />
        <button type="submit">Add Debit</button>
      </form>
      <br/>
      <Link to="/">Return to Home</Link>
    </div>
  );
}

const AccountBalance = (props) => {
  const calculateBalance = () => {
    const totalCredits = props.credits?.reduce((sum, credit) => sum + credit.amount, 0) || 0;
    const totalDebits = props.debits?.reduce((sum, debit) => sum + debit.amount, 0) || 0;
    return totalCredits - totalDebits;
  }

  const formatBalance = (balance) => {
    const absBalance = Math.abs(balance);
    const formattedNumber = absBalance.toFixed(2);
    return balance < 0 ? `-$${formattedNumber}` : `$${formattedNumber}`;
  }

  const currentBalance = calculateBalance();
  const formattedBalance = formatBalance(currentBalance);

  return (
    <div>
      <h2>Account Balance</h2>
      <div>{formattedBalance}</div>
    </div>
  );
}

export default Debits;
