/*==================================================
src/components/AccountBalance.js

The AccountBalance component displays account balance. It is included in other page views.
==================================================*/
import React, {Component} from 'react';

class AccountBalance extends Component {
  // Calculate the current balance from credits and debits
  calculateBalance = () => {
    const totalCredits = this.props.credits?.reduce((sum, credit) => sum + credit.amount, 0) || 0;
    const totalDebits = this.props.debits?.reduce((sum, debit) => sum + debit.amount, 0) || 0;
    return totalCredits - totalDebits;
  }

  // Format the balance to 2 decimal places and handle negative numbers
  formatBalance = (balance) => {
    // Convert to absolute value for formatting
    const absBalance = Math.abs(balance);
    // Format to exactly 2 decimal places
    const formattedNumber = absBalance.toFixed(2);
    // Add negative sign if needed
    return balance < 0 ? `-$${formattedNumber}` : `$${formattedNumber}`;
  }

  render() {
    const currentBalance = this.calculateBalance();
    const formattedBalance = this.formatBalance(currentBalance);

    return (
      <div className="balance-display">
        <h2>Account Balance</h2>
        <div className={`balance-amount ${currentBalance < 0 ? 'negative' : 'positive'}`}>
          {formattedBalance}
        </div>
      </div>
    );
  }
}

export default AccountBalance;
